import React from "react";
import { Container, Row, Col, Image } from "react-bootstrap";
import { useParams } from "react-router-dom";
import CoursesData from "./../../CoursesData";

export default function CourseDetail() {
  let params = useParams();
  let courseData = CoursesData.find((course) => params.CourseID == course.id);
  console.log(courseData);

  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
          <Image style={{ boxShadow: "1px 1px 8px rgba(0, 0, 0, 0.07)",  }} src={courseData.img} fluid/>
        </Col>
        <Col md={6} className="d-flex flex-column justify-content-around align-items-center">
          <h2>{courseData.title}</h2>
          <p className="text-right">{courseData.description}</p>
          <p>{courseData.price.toLocaleString()} تومان</p>
        </Col>
      </Row>
    </Container>
  );
}
