import React from "react";
import { Container, Navbar, Nav, Button, Form } from "react-bootstrap";
import { useState } from "react-dom";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import LoginTwoToneIcon from "@mui/icons-material/LoginTwoTone";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";
import SearchIcon from "@mui/icons-material/Search";
import "./Header.css";
import MegaMenu from "../MegaMenu/MegaMenu";

export default function Header() {
  const [showMegaMenu, setShowMegaMenu] = useState(false);

  return (
    <Navbar expand="lg" className="pt-0 pb-0 navbar">
      <Container fluid className="justify-content-between" style={{ gap: 60 }}>
        {/* لوگو */}
        <Navbar.Brand className="logo">شادی لند</Navbar.Brand>

        {/* دکمه منوی موبایل */}
        <Navbar.Toggle aria-controls="main-navbar" />

        <Navbar.Collapse id="main-navbar" className="justify-content-between">
          {/* منوی راست‌چین */}
          <Nav className="me-auto nav-font align-self-end" dir="rtl">
            <div className="d-flex" style={{ gap: 10 }}>
              <Nav.Link href="/">خانه</Nav.Link>
              <Nav.Link
                href="#"
                onMouseEnter={() => setShowMegaMenu(true)}
                onMouseLeave={() => setShowMegaMenu(false)}
              >
                محصولات
                <KeyboardArrowDownIcon />
              </Nav.Link>

              <Nav.Link href="/about">درباره ما</Nav.Link>
              <Nav.Link href="/contact">تماس با ما</Nav.Link>
            </div>
          </Nav>

          {/* نوار جستجو */}
          <Form
            className="search-container"
            style={{ flex: 1, maxWidth: "600px" }}
          >
            <div className="search-box-wrapper">
              <SearchIcon className="search-icon" />
              <Form.Control
                type="search"
                placeholder="جستجوی محصولات..."
                className="search-box"
                aria-label="Search"
              />
            </div>
          </Form>

          {/* آیکون‌ها */}
          <div className="d-flex align-items-center" style={{ gap: 15 }}>
            <Button className="d-flex align-items-center text-white login-register-btn">
              <LoginTwoToneIcon />
              ورود | ثبت‌نام
            </Button>

            <Button variant="light" className="position-relative cart-btn">
              <ShoppingCartIcon fontSize="large" />
            </Button>
          </div>
        </Navbar.Collapse>
        
      </Container>
    </Navbar>
  );
}
