import React, { useState } from "react";
import "./MegaMenu.css";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const categories = [
  {
    title: "تم‌ها",
    subItems: ["تم دخترانه", "تم پسرانه", "تم بزرگسال"]
  },
  {
    title: "لوازم تولد",
    subItems: ["بادکنک", "شمع تولد", "کلاه تولد"]
  },
  {
    title: "لوازم کمکی",
    subItems: ["نی نوشیدنی", "چنگال و کارد", "لیوان"]
  },
  {
    title: "آتش‌بازی",
    subItems: ["فشفشه", "آبشار", "کاغذ رنگی"]
  }
];

export default function MegaMenu({ show }) {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <div className={`mega-menu-container ${show ? "show" : ""}`}>
      <div className="mega-menu">
        <div className="categories">
          {categories.map((category, index) => (
            <div
              key={index}
              className="category-item"
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
            >
              <span className="category-label">
                {category.title}
              
              </span>

              {hoveredIndex === index && (
                <div className="sub-menu">
                  {category.subItems.map((subItem, subIndex) => (
                    <div key={subIndex} className="sub-item">
                      {subItem}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
