// src/components/MegaMenu/MegaMenu.js
import React from 'react';
import { MegaMenu } from 'primereact/megamenu';
import 'primereact/resources/themes/lara-light-blue/theme.css'; // یا هر تمی که انتخاب کردی
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

export default function CustomMegaMenu() {
  const items = [
    {
      label: 'تم‌ها',
      icon: 'pi pi-star',
      items: [
        [
          {
            label: 'تم دخترانه',
            items: [{ label: 'پرنسس' }, { label: 'یونیکورن' }]
          }
        ],
        [
          {
            label: 'تم پسرانه',
            items: [{ label: 'ماشین‌ها' }, { label: 'بتمن' }]
          }
        ]
      ]
    },
    {
      label: 'لوازم تولد',
      icon: 'pi pi-gift',
      items: [
        [
          {
            label: 'بادکنک',
            items: [{ label: 'رنگی' }, { label: 'متالیک' }]
          }
        ],
        [
          {
            label: 'کلاه تولد',
            items: [{ label: 'کلاسیک' }, { label: 'طرح‌دار' }]
          }
        ]
      ]
    },
    {
      label: 'آتش‌بازی',
      icon: 'pi pi-fire',
      items: [
        [
          {
            label: 'فشفشه',
            items: [{ label: 'کوچک' }, { label: 'بزرگ' }]
          }
        ]
      ]
    }
  ];

  return (
    <div className="custom-megamenu-wrapper">
      <MegaMenu model={items} orientation="horizontal" breakpoint="960px" />
    </div>
  );
}
