import React, { useState } from "react";
import "./MegaMenu.css";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const categories = [
  {
    title: "تم‌ها",
    subItems: [
      {
        title: "تم دخترانه",
        subItems: ["پرنسسی", "یونیکورن", "فروزن"]
      },
      {
        title: "تم پسرانه",
        subItems: ["ماشین‌ها", "مرد عنکبوتی", "بتمن"]
      }
    ]
  },
  {
    title: "لوازم تولد",
    subItems: [
      {
        title: "بادکنک",
        subItems: ["معمولی", "هلیومی"]
      },
      {
        title: "شمع تولد",
        subItems: ["شمع عدد", "شمع فانتزی"]
      }
    ]
  }
];


export default function MegaMenu({ show }) {
  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <div className={`mega-menu-container ${show ? "show" : ""}`}>
      <div className="mega-menu">
        <div className="categories">
          {categories.map((category, index) => (
            <div
              key={index}
              className="category-item"
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
            >
              <span className="category-label">
                {category.title}
                <KeyboardArrowLeftIcon />
              </span>

              {hoveredIndex === index && (
  <div className="sub-menu">
    {category.subItems.map((sub, subIdx) => (
      <div
        key={subIdx}
        className="sub-item"
        onMouseEnter={() => setHoveredIndex(`${index}-${subIdx}`)}
        onMouseLeave={() => setHoveredIndex(index)}
        style={{ position: "relative" }}
      >
        {sub.title}
        {/* نمایش زیرمنوی سطح سوم */}
        {hoveredIndex === `${index}-${subIdx}` && sub.subItems && (
          <div className="third-menu">
            {sub.subItems.map((item, i) => (
              <div key={i} className="sub-item">
                {item}
              </div>
            ))}
          </div>
        )}
      </div>
    ))}
  </div>
)}

            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
