// MegaMenu.js
import React, { useState } from "react";
import "./MegaMenu.css";

const categories = [
  {
    title: "تم‌ها",
    subItems: ["تم دخترانه", "تم پسرانه", "تم بزرگسال"]
  },
  {
    title: "لوازم تولد",
    subItems: ["بادکنک", "شمع تولد", "کلاه تولد"]
  },
  {
    title: "لوازم کمکی",
    subItems: ["نی نوشیدنی", "چنگال و کارد", "لیوان"]
  },
  {
    title: "آتش‌بازی",
    subItems: ["فشفشه", "آبشار", "کاغذ رنگی"]
  }
];

export default function MegaMenu({ show }) {
    return (
      <div className="mega-menu-container" style={{ position: "absolute", top: "100%", left: 0, right: 0 }}>
        <div className={`mega-menu ${show ? "show" : ""}`}>
          {categories.map((category, index) => (
            <div key={index} className="mega-column">
              <h4>{category.title}</h4>
              <ul>
                {category.subItems.map((item, i) => (
                  <li key={i}><a href="#">{item}</a></li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    );
  }
