import React from "react";
import "../../components/Header/";

const categories = [
  {
    title: "تم‌ها",
    subItems: ["تم دخترانه", "تم پسرانه", "تم بزرگسال", "تم کودک", "تم شخصیت کارتونی"]
  },
  {
    title: "لوازم تولد",
    subItems: ["بادکنک", "شمع تولد", "کلاه تولد", "ریسه", "آویز"]
  },
  {
    title: "لوازم پذیرایی",
    subItems: ["بشقاب", "لیوان", "قاشق و چنگال", "سفره یکبار مصرف", "دستمال"]
  },
  {
    title: "آتش‌بازی",
    subItems: ["فشفشه", "آبشار", "کاغذ رنگی", "ترقه مجاز", "شمع جادویی"]
  },
  {
    title: "هدایا",
    subItems: ["کادوی آماده", "جعبه کادو", "روبان", "کارت تبریک"]
  },
  {
    title: "بادکنک‌آرایی",
    subItems: ["بادکنک هلیومی", "پمپ بادکنک", "کیسه بادکنک", "پایه بادکنک"]
  }
];

export default function CustomMegaMenu({ show, onClose }) {
  return (
    <>
      {show && (
        <div className="mega-menu-overlay" onMouseLeave={onClose}>
          <div className="mega-menu-box" dir="rtl">
            <div className="main-categories">
              {categories.map((cat, index) => (
                <div className="main-category" key={index}>
                  <div className="category-title">{cat.title}</div>
                  <ul className="sub-category-list">
                    {cat.subItems.map((sub, i) => (
                      <li key={i}>{sub}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
}
