import React from "react";
import { Container, Navbar, Nav, Button, Form, InputGroup, FormControl } from "react-bootstrap";
import ShoppingCartTwoToneIcon from "@mui/icons-material/ShoppingCartTwoTone";
import LoginTwoToneIcon from "@mui/icons-material/LoginTwoTone";
import SearchIcon from "@mui/icons-material/Search";
import "./Header.css";
import "../../assets/Css/Font.css";

export default function Header() {
  return (
    <Navbar bg="light" expand="lg" className="shadow-sm">
      <Container fluid className="justify-content-between" style={{ gap: 30 }}>
        {/* لوگو */}
        <Navbar.Brand className="logo">شادی لند</Navbar.Brand>

        {/* دکمه منوی موبایل */}
        <Navbar.Toggle aria-controls="main-navbar" />

        <Navbar.Collapse id="main-navbar" className="justify-content-between">
          {/* منوی راست‌چین */}
          <Nav className="me-auto nav-font" dir="rtl">
            <div className="d-flex">
              <Nav.Link href="/">خانه</Nav.Link>
              <Nav.Link href="/products">محصولات</Nav.Link>
              <Nav.Link href="/about">درباره ما</Nav.Link>
              <Nav.Link href="/contact">تماس با ما</Nav.Link>
            </div>
          </Nav>

          {/* نوار جستجو با آیکون داخل */}
          <InputGroup style={{ maxWidth: "600px", flex: 1 }}>
            <InputGroup.Text>
              <SearchIcon />
            </InputGroup.Text>
            <FormControl
              placeholder="جستجوی محصولات..."
              aria-label="Search"
              className="search-box"
            />
          </InputGroup>

          {/* آیکون‌ها */}
          <div className="d-flex align-items-center gap-3">
            <Button className="d-flex align-items-center gap-2 text-white" variant="primary">
              <LoginTwoToneIcon />
              ورود | ثبت‌نام
            </Button>

            <Button variant="light" className="position-relative">
              <ShoppingCartTwoToneIcon />
            </Button>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
