import React from "react";
import "./Form.css";

export default class App extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            firstNameData: '',
            lastNameData: '',
            emailData: '',
            submitted: false,
            allValid: false
        }
    }

    onChangeHandlerName (){
this.setState()
    }
    onChangeHandlerLastName(){
this.setState()
    }
    onChangeHandlerEmail(){
this.setState()
    }
    onSubmitHandler(){
        this.setState()
    }

    render() {
        return (
            <div className="form-container">
                <form className="register-form" autoComplete="off">
                    {/* Uncomment the next line to show the success message */}
                    <div className="success-message">Success! Thank you for registering</div>
                    <input
                        id="first-name"
                        className="form-field"
                        type="text"
                        placeholder="First Name"
                        name="firstName"
                        onChange={this.onChangeHandlerName}
                    />
                    {/* Uncomment the next line to show the error message */}
                    <span id="first-name-error">Please enter a first name</span>
                    <input
                        id="last-name"
                        className="form-field"
                        type="text"
                        placeholder="Last Name"
                        name="lastName"
                        onChange={this.onChangeHandlerLastName}
                    />
                    {/* Uncomment the next line to show the error message */}
                    <span id="last-name-error">Please enter a last name</span>
                    <input
                        id="email"
                        className="form-field"
                        type="text"
                        placeholder="Email"
                        name="email"
                        onChange={this.onChangeHandlerEmail}
                    />
                    {/* Uncomment the next line to show the error message */}
                    <span id="email-error">Please enter an email address</span>
                    <button className="form-field" type="submit" onSubmit={this.onSubmitHandler}>
                        Register
                    </button>
                </form>
            </div>

        )
    }
}
