import React from "react";
import { Container, Navbar, Nav, Button, Form } from "react-bootstrap";
import ShoppingCartTwoToneIcon from "@mui/icons-material/ShoppingCartTwoTone";
import LoginTwoToneIcon from "@mui/icons-material/LoginTwoTone";
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import SearchIcon from '@mui/icons-material/Search';
import "./Header.css";

export default function Header() {
  return (
    <Navbar expand="lg" className="pt-0 pb-0 navbar">
      <Container fluid className="justify-content-between" style={{ gap: 40 }}>
        {/* لوگو */}
        <Navbar.Brand className="logo">شادی لند</Navbar.Brand>

        {/* دکمه منوی موبایل */}
        <Navbar.Toggle aria-controls="main-navbar" />

        <Navbar.Collapse id="main-navbar" className="justify-content-between">
          {/* منوی راست‌چین */}
          <Nav className="me-auto nav-font" dir="rtl">
            <div className="d-flex" style={{ gap: 5 }}>
              <Nav.Link href="/">خانه</Nav.Link>
              <Nav.Link href="/products">محصولات<KeyboardArrowDownIcon /></Nav.Link>
              <Nav.Link href="/about">درباره ما</Nav.Link>
              <Nav.Link href="/contact">تماس با ما</Nav.Link>
            </div>
          </Nav>

          {/* نوار جستجو */}
          <Form className="d-flex" style={{ flex: 1, maxWidth: "600px" }}>
            <Form.Control
              type="search"
              placeholder="جستجوی محصولات..."
              className="me-2 search-box"
              aria-label="Search"
              
            />
          </Form>

          {/* آیکون‌ها */}
          <div className="d-flex align-items-center gap-3">
            <Button className="d-flex align-items-center gap-2 text-white" variant="primary">
              <LoginTwoToneIcon />
              ورود | ثبت‌نام
            </Button>

            <Button variant="light" className="position-relative">
              <ShoppingCartTwoToneIcon size={20} />
            </Button>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
