import React from "react";
import "./Form.css";

export default class App extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            firstNameData: '',
            lastNameData: '',
            emailData: '',
            submitted: false,
            allValid: false
        }
    }

    onChangeHandlerName (event){
        console.log(event)
        this.setState({
            firstNameData : event.nativeEvent.target.value
        })
    }
    onChangeHandlerLastName(event){
        this.setState({
            lastNameData : event.nativeEvent.target.value

        })
    }
    onChangeHandlerEmail(event){
        this.setState({
            emailData : event.nativeEvent.target.value

        })
    }
    onClickHandler(event){
        event.preventDefault();
        this.setState({
            submitted: true
        })
    }

    render() {
        return (
            <div className="form-container">
                <form className="register-form" autoComplete="off">
                    {/* Uncomment the next line to show the success message */}
                    <div className="success-message">Success! Thank you for registering</div>
                    <input
                        id="first-name"
                        className="form-field"
                        type="text"
                        placeholder="First Name"
                        name="firstName"
                        onChange={(event) => this.onChangeHandlerName(event)}
                    />
                    {/* Uncomment the next line to show the error message */}
                    {!this.state.firstNameData && (
                        <span id="first-name-error">Please enter a first name</span>
                    )}
                    <input
                        id="last-name"
                        className="form-field"
                        type="text"
                        placeholder="Last Name"
                        name="lastName"
                        onChange={(event) => this.onChangeHandlerLastName(event)}
                    />
                    {/* Uncomment the next line to show the error message */}
                    {!this.state.lastNameData && (
                    <span id="last-name-error">Please enter a last name</span>
                    )}
                    <input
                        id="email"
                        className="form-field"
                        type="text"
                        placeholder="Email"
                        name="email"
                        onChange={(event) => this.onChangeHandlerEmail(event)}
                    />
                    {/* Uncomment the next line to show the error message */}
                    {!this.state.emailData && (
                        <span id="email-error">Please enter an email address</span>
                    )}
                    <button className="form-field" type="submit" onClick={(event) => this.onClickHandler(event)}>
                        Register
                    </button>
                </form>
            </div>

        )
    }
}
