import React, { Component } from 'react'
import Book from './Book'

export default class AddForm extends Component {

    constructor() {
        super()

        this.state = {
            books: [],
            title: '',
            author: '',
            year: '',
        }

    }
    
    titleHandler (event){
        this.setState({
            title : event.target.value
        })
    }
    authorHandler (event){
        this.setState({
            author : event.target.value
        })
    }
    yearHandler (event){
        this.setState({
            year : event.target.value
        })
    }

    submitHandler (event){
        event.preventDefault()
        const allValid = this.state.title !== '' && this.state.author !== '' && this.state.year !== '' 
        if (allValid){
            const newBook = {
                title: this.state.title,
                author: this.state.author,
                year: this.state.year
            };
            this.setState((prevState) => {
                return{
                    books : [...prevState.books,newBook],
                    title: '',
                    author: '',
                    year: ''
                }
            })
        }
    }
    render() {
        return (
            <>
                <form id="book-form" autoComplete="off">
                    <div className="form-group">
                        <label htmlFor="title">Title</label>
                        <input type="text" id="title" value={this.state.title} className="form-control" onChange={(event) => this.titleHandler(event)}/>
                    </div>

                    <div className="form-group">
                        <label htmlFor="author">Author</label>
                        <input type="text" id="author" value={this.state.author} className="form-control" onChange={(event) => this.authorHandler(event)}/>
                    </div>

                    <div className="form-group">
                        <label htmlFor="year">Year</label>
                        <input type="text" id="year" value={this.state.year} className="form-control" onChange={(event) => this.yearHandler(event)}/>
                    </div>
                    <input type="submit" value="Add Book" className="btn btn-warning btn-block add-btn"
                    onClick={(event) => this.submitHandler(event)}/>
                </form>
                <table className="table table-striped mt-5 text-center">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Author</th>
                            <th>Year</th>
                        </tr>
                    </thead>
                    <tbody id="book-list">
                    {this.state.books.map((book, index) => (
                        <Book
                            key={index}
                            title={book.title}
                            author={book.author}
                            year={book.year}
                        />
                    ))}
                    </tbody>
                </table>


            </>
        )
    }
}
