import React from "react";
import { Container, Navbar, Nav, Button } from "react-bootstrap";
import ShoppingCartTwoToneIcon from "@mui/icons-material/ShoppingCartTwoTone";
import LoginTwoToneIcon from "@mui/icons-material/LoginTwoTone";
import "./Header.css"; // استایل دلخواهت

export default function Header() {
  return (
    <Navbar bg="light" expand="lg" className="shadow-sm">
      <Container fluid className="justify-content-between">
        {/* برند یا لوگو */}
        <Navbar.Brand className="fw-bold fs-4">شادی لند</Navbar.Brand>

        {/* دکمه نمایش منو در موبایل */}
        <Navbar.Toggle aria-controls="main-navbar" />

        <Navbar.Collapse id="main-navbar" className="justify-content-between">
          {/* منوی راست‌چین */}
          <Nav className="d-flex me-auto gap-1" dir="rtl">
            <Nav.Link href="/">خانه</Nav.Link>
            <Nav.Link href="/products">محصولات</Nav.Link>
            <Nav.Link href="/about">درباره ما</Nav.Link>
            <Nav.Link href="/contact">تماس با ما</Nav.Link>
          </Nav>

          {/* سمت چپ: آیکون‌ها */}
          <div className="d-flex align-items-center gap-3">
            <Button className="d-flex align-items-start justify-content-center text-white">
              <LoginTwoToneIcon />
              ورود | ثبت‌نام
            </Button>

            <Button variant="light" className="position-relative">
              <ShoppingCartTwoToneIcon size={20} />
            </Button>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
