import React, { useState, useEffect } from 'react';

export default function MyComponent() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  }, []);

  return (
    <div>

        <div>
          <h1>عنوان واقعی</h1>
          <img src="https://via.placeholder.com/300x200" alt="واقعی" />
          <p>توضیحات واقعی لود شده!</p>
        </div>
      
    </div>
  );
}
