import React, { Component } from 'react'

export default class Product extends Component {
    

    addToCart = (id) => {
        const product = this.state.products.find(p => p.id === id);
        if (product) {
          this.setState(prevState => ({
            shoppingCart: [...prevState.shoppingCart, product]
          }));
        }
        console.log(shoppingCart);
        
      }
      
    render() {
        return (
            <div class="shop-item">
                <span class="shop-item-title">{this.props.title}</span>
                <img class="shop-item-image" src={this.props.image} />
                <div class="shop-item-details">
                    <span class="shop-item-price">${this.props.price}</span>
                    <button
                        class="btn btn-primary shop-item-button"
                        type="button" onClick={() => this.props.onAddToCart(this.props.id)}>
                        ADD TO CART
                    </button>
                </div>
            </div>
        )
    }
}
