// MegaMenu.js
import React, { useState } from "react";
import "./MegaMenu.css";

const categories = [
  {
    title: "تم‌ها",
    subItems: ["تم دخترانه", "تم پسرانه", "تم بزرگسال"]
  },
  {
    title: "لوازم تولد",
    subItems: ["بادکنک", "شمع تولد", "کلاه تولد"]
  },
  {
    title: "لوازم کمکی",
    subItems: ["نی نوشیدنی", "چنگال و کارد", "لیوان"]
  },
  {
    title: "آتش‌بازی",
    subItems: ["فشفشه", "آبشار", "کاغذ رنگی"]
  }
];

export default function MegaMenu({ show }) {
  return (
    <div className={`mega-menu-container ${show ? "show" : ""}`}>
      <div className="mega-menu">
        {categories.map((category, index) => (
          <div key={index} className="mega-menu-column">
            <h4>{category.title}</h4>
            <ul>
              {category.subItems.map((item, i) => (
                <li key={i}>{item}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
