import React from "react";
import { Container, Row, Col, Image } from "react-bootstrap";
import { useParams } from "react-router-dom";
import CoursesData from "./../../CoursesData";

export default function CourseDetail() {
  const { CourseID } = useParams();
  const courseData = CoursesData.find((course) => CourseID == course.id);

  if (!courseData) return <h2>Course not found</h2>;

  return (
    <Container className="mt-5">
      <Row>
        {/* تصویر در ستون سمت چپ */}
        <Col>
          <Image src={courseData.img} fluid />
        </Col>

        {/* مشخصات محصول در ستون سمت راست */}
        <Col>
          <h2>{courseData.title}</h2>
          <p>
            <strong>Price:</strong> {courseData.price.toLocaleString()} تومان
          </p>
          <p>
            در این دوره شما با {courseData.title} آشنا می‌شوید و می‌توانید از آن
            در پروژه‌های واقعی استفاده کنید.
          </p>
        </Col>
      </Row>
    </Container>
  );
}
