import React, { useState, useEffect } from 'react';
import Skeleton from '@mui/material/Skeleton';
import Box from '@mui/material/Box';

function MyComponent() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000);
    
    return () => clearTimeout(timer); // Cleanup timer
  }, []);

  return (
    <Box sx={{ padding: 2 }}>
      {isLoading ? (
        <Box>
          <Skeleton variant="text" width={210} height={40} animation="wave" />
          <Skeleton variant="rectangular" width={300} height={200} sx={{ my: 2 }} animation="wave" />
          <Skeleton variant="circular" width={40} height={40} animation="wave" />
        </Box>
      ) : (
        <Box>
          <h1>عنوان واقعی</h1>
          <img src="https://via.placeholder.com/300x200" alt="واقعی" style={{ margin: '16px 0' }} />
          <p>توضیحات واقعی لود شده!</p>
        </Box>
      )}
    </Box>
  );
}

export default MyComponent;
