import React, { useState } from "react";
import "./MegaMenu.css";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";

const categories = [
  {
    title: "تم‌ها",
    subItems: [
      { title: "تم دخترانه", subItems: ["پرنسسی", "یونیکورن", "فروزن"] },
      { title: "تم پسرانه", subItems: ["ماشین‌ها", "مرد عنکبوتی", "بتمن"] },
    ],
  },
  {
    title: "لوازم تولد",
    subItems: [
      { title: "بادکنک", subItems: ["معمولی", "هلیومی"] },
      { title: "شمع تولد", subItems: ["شمع عدد", "شمع فانتزی"] },
    ],
  },
];

export default function MegaMenu({ show }) {
  const [hoveredMainIndex, setHoveredMainIndex] = useState(null);
  const [hoveredSubIndex, setHoveredSubIndex] = useState(null);

  return (
    <div
      className={`mega-menu-container ${show ? "show" : ""}`}
      onMouseLeave={() => {
        setHoveredMainIndex(null);
        setHoveredSubIndex(null);
      }}
    >
      <div className="mega-menu">
        <div className="categories">
          {categories.map((category, mainIdx) => (
            <div
              key={mainIdx}
              className="category-item"
              onMouseEnter={() => setHoveredMainIndex(mainIdx)}
            >
              <span className="category-label">
                {category.title}
                <KeyboardArrowLeftIcon />
              </span>

              {hoveredMainIndex === mainIdx && (
                <div className="sub-menu">
                  {category.subItems.map((sub, subIdx) => (
                    <div
                      key={subIdx}
                      className="sub-item"
                      onMouseEnter={() => setHoveredSubIndex(subIdx)}
                      onMouseLeave={() => setHoveredSubIndex(null)}
                      style={{ position: "relative" }}
                    >
                      {sub.title}

                      {hoveredSubIndex === subIdx && sub.subItems && (
                        <div className="third-menu">
                          {sub.subItems.map((item, i) => (
                            <div key={i} className="sub-item">
                              {item}
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
