import React, { useState } from "react";
import "./MegaMenu.css";

const categories = [
  {
    title: "لباس مردانه",
    subItems: [
      {
        title: "هودی مردانه",
        items: ["کلاه‌دار", "بدون کلاه"]
      },
      {
        title: "پیرهن مردانه",
        items: ["رسمی", "اسپرت"]
      }
    ]
  },
  {
    title: "لباس زنانه",
    subItems: [
      {
        title: "شومیز زنانه",
        items: ["مجلسی", "راحتی"]
      },
      {
        title: "مانتو",
        items: ["پاییزه", "تابستانه"]
      }
    ]
  },
  {
    title: "لوازم تولد",
    subItems: [
      {
        title: "بادکنک",
        items: ["بادکنک ساده", "بادکنک فویلی"]
      },
      {
        title: "شمع تولد",
        items: ["عددی", "فانتزی"]
      }
    ]
  }
];

export default function MegaMenu({ show, onClose }) {
  const [activeCategory, setActiveCategory] = useState(null);

  return (
    <>
      {show && <div className="mega-menu-backdrop" onMouseEnter={onClose} />}
      <div
        className={`mega-menu-container ${show ? "show" : ""}`}
        onMouseLeave={onClose}
      >
        <div className="mega-menu-content">
          <div className="mega-menu-main">
            {categories.map((cat, index) => (
              <div
                key={index}
                className={`mega-menu-item ${
                  activeCategory === index ? "active" : ""
                }`}
                onMouseEnter={() => setActiveCategory(index)}
              >
                {cat.title}
              </div>
            ))}
          </div>

          <div className="mega-menu-sub">
            {activeCategory !== null &&
              categories[activeCategory].subItems.map((sub, i) => (
                <div key={i} className="sub-category-column">
                  <h4>{sub.title}</h4>
                  <ul>
                    {sub.items.map((item, j) => (
                      <li key={j}>{item}</li>
                    ))}
                  </ul>
                </div>
              ))}
          </div>
        </div>
      </div>
    </>
  );
}
