import React from "react";
import "./Form.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstNameData: '',
      lastNameData: '',
      emailData: '',
      submitted: false,
      allValid: false
    };
  }

  // استفاده از Arrow Function تا نیاز به bind نباشه
  onChangeHandlerName = (event) => {
    this.setState({ firstNameData: event.target.value });
  }

  onChangeHandlerLastName = (event) => {
    this.setState({ lastNameData: event.target.value });
  }

  onChangeHandlerEmail = (event) => {
    this.setState({ emailData: event.target.value });
  }

  onSubmitHandler = (event) => {
    event.preventDefault();

    const { firstNameData, lastNameData, emailData } = this.state;

    const allValid = firstNameData !== '' && lastNameData !== '' && emailData !== '';

    this.setState({
      submitted: true,
      allValid: allValid
    });
  }

  render() {
    const { firstNameData, lastNameData, emailData, submitted, allValid } = this.state;

    return (
      <div className="form-container">
        <form className="register-form" autoComplete="off">
          {submitted && allValid && (
            <div className="success-message">Success! Thank you for registering</div>
          )}

          <input
            id="first-name"
            className="form-field"
            type="text"
            placeholder="First Name"
            name="firstName"
            onChange={this.onChangeHandlerName}
          />
          {submitted && !firstNameData && (
            <span id="first-name-error">Please enter a first name</span>
          )}

          <input
            id="last-name"
            className="form-field"
            type="text"
            placeholder="Last Name"
            name="lastName"
            onChange={this.onChangeHandlerLastName}
          />
          {submitted && !lastNameData && (
            <span id="last-name-error">Please enter a last name</span>
          )}

          <input
            id="email"
            className="form-field"
            type="text"
            placeholder="Email"
            name="email"
            onChange={this.onChangeHandlerEmail}
          />
          {submitted && !emailData && (
            <span id="email-error">Please enter an email address</span>
          )}

          <button className="form-field" type="submit" onClick={this.onSubmitHandler}>
            Register
          </button>
        </form>
      </div>
    );
  }
}
